%%  ============== Main function: kinematics=======================
    function [x_out,y_out,MSE,x,y] = kinematics(x,type,N)
        % -----------------------------------------------------------------
        % guolong on 20210710
        % Kinematic analysis of linkage mechanism
        % Input variables:
        % x: design variables
        % Four-bar mechanism: [xa ya r1 r2 r3 r4 a1 b1 beta gama_x] /rad
        % Six-bar mechanism: [xa ya r1 r2 r3 r4 a1 b1 a2 b2 r9 r10 a3 b3 beta gama_x] /rad
        % tpye: '4', 'watt1', 'ste1', 'ste2', 'ste3'
        % N: Number of discrete points of trajectory, default is 360
        % xh yh: Output trajectory coordinates
        % -----------------------------------------------------------------

    %...Determine whether N is entered
        if N ~= []
            N = 360;
        end
    %...Determine the type of linkages
        switch type  
            %------------------------------------------------------------------
            case '4'
                if length(x) ~= 10
                   disp('The input variable length is incorrect, the correct length is 10 variables')
                   disp('[xa ya r1 r2 r3 r4 a1 b1 beta gama_x] /rad ')
                else
                   [x_out,y_out,MSE,x,y] = four(x,N);
                end
            %------------------------------------------------------------------    
            case 'watt1'
                if length(x) ~= 16
                   disp('The input variable length is incorrect, the correct length is 16 variables')
                   disp('[xa ya r1 r2 r3 r4 a1 b1 a2 b2 r9 r10 a3 b3 beta gama_x] /rad ')
                else
                    [x_out,y_out] = watt1(x,N);
                end 
            %------------------------------------------------------------------    
            case 'ste1'
                if length(x) ~= 16
                   disp('The input variable length is incorrect, the correct length is 16 variables')
                   disp('[xa ya r1 r2 r3 r4 a1 b1 a2 b2 r9 r10 a3 b3 beta gama_x] /rad ')
                else
                    [x_out,y_out,MSE,x,y] = ste1(x,N);
                end 
            %------------------------------------------------------------------     
            case 'ste2'
                if length(x) ~= 16
                   disp('The input variable length is incorrect, the correct length is 16 variables')
                   disp('[xa ya r1 r2 r3 r4 a1 b1 a2 b2 r9 r10 a3 b3 beta gama_x] /rad ')
                else
                    [x_out,y_out,MSE,x,y] = ste2(x,N);
                end 
            %------------------------------------------------------------------     
            case 'ste2_fast'
                if length(x) ~= 16
                   disp('The input variable length is incorrect, the correct length is 16 variables')
                   disp('[xa ya r1 r2 r3 r4 a1 b1 a2 b2 r9 r10 a3 b3 beta gama_x = 0] /rad ')
                else
                   [x_out,y_out,MSE,x,y] = ste2_fast(x,N);
                end 
            case 'ste3'
                if length(x) ~= 16
                   disp('The input variable length is incorrect, the correct length is 16 variables')
                   disp('[xa ya r1 r2 r3 r4 a1 b1 a2 b2 r9 r10 a3 b3 beta gama_x] /rad ')
                else
                    [x_out,y_out,MSE,x,y] = ste3(x,N);
                end          
        end   

    end
 
 
