%-------------------------subsubfunction 2、Watt1 -------------------------
    function [xh,yh,MSE,x,y] = watt1(x,N)
        xa = x(1);     ya = x(2);    r1 = x(3);    r2 = x(4);
        r3 = x(5);     r4 = x(6);    a1 = x(7);    b1 = x(8);
        a2 = x(9);     b2 = x(10);   r9 = x(11);   r10 = x(12);
        a3 = x(13);    b3 = x(14);
        beta = x(15);    gama_x = x(16);   % rad 

        delta_phi = 2*pi/N;  % rad
        xh = [];
        yh = [];
        j = 0;
        for phi =  gama_x  : delta_phi : 2*pi + gama_x - delta_phi  
            j = j+1;
            [xb,yb] = crank(xa,ya,r1,phi);
            xd = xa + r4*cos(beta);
            yd = ya + r4*sin(beta);
            [xc,yc,phiBC,phiDC] = RRR(xb,yb,xd,yd,r2,r3,1);
            [xe,ye] = rigid_body(xb,yb,phiBC,a1,b1,1);
            [xf,yf] = rigid_body(xd,yd,phiDC,a2,b2,-1);    
            [xg,yg,phiEG,phiFG] = RRR(xe,ye,xf,yf,r10,r9,1);
            [xh(j),yh(j)] = rigid_body(xe,ye,phiEG,a3,b3,1);      
        end
        x = [xa,xb,xc,xd,xe,xf,xg,xh(j)];
