    function [xh,yh,MSE,x,y] = ste2(x,N)
        xa = x(1);     ya = x(2);    r1 = x(3);    r2 = x(4);
        r3 = x(5);     r4 = x(6);    a1 = x(7);    b1 = x(8);
        a2 = x(9);     b2 = x(10);   r9 = x(11);   r10 = x(12);
        a3 = x(13);    b3 = x(14);
        beta = x(15);    gama_x = x(16);   % rad 

        delta_phi = 2*pi/N;  % rad
        xh = [];
        yh = [];
        j = 0;
        phibc = [];
        for phi =  gama_x  : delta_phi : 2*pi + gama_x - delta_phi  
            j = j+1;
            disp(['正在计算第',num2str(j),' / ',num2str(N),'个轨迹离散点'])
            [xb,yb] = crank(xa,ya,r1,phi);    
            xe = xa + r4*cos(beta);
            ye = ya + r4*sin(beta);
            % -----------计算phibc----------
            if  j == 1
                syms phiBC
                [xc,yc] = crank(xb,yb,r2,phiBC);
                [xf,yf] = rigid_body(xb,yb,phiBC,a1,b1,1);
                [xd,yd,phiCD,phiED] =  RRR(xc,yc,xe,ye,r9,r3,1);
                [xg,yg] = rigid_body(xe,ye,phiED,a2,b2,-1);
                fg = sqrt(  ( xf-xg)^2 + ( yf-yg)^2   );
                eq1 = fg - r10 == 0;
                phibc(j,:) = double(vpasolve(eq1,phiBC,[-pi pi]));
                phibc(j,:) = phibc(1);
            else
                syms phiBC
                [xc,yc] = crank(xb,yb,r2,phiBC);
                [xf,yf] = rigid_body(xb,yb,phiBC,a1,b1,1);
                [xd,yd,phiCD,phiED] =  RRR(xc,yc,xe,ye,r9,r3,1);
                [xg,yg] = rigid_body(xe,ye,phiED,a2,b2,-1);
                fg = sqrt(  ( xf-xg)^2 + ( yf-yg)^2   );
                eq1 = fg - r10 == 0;
                phibc(j) = double(vpasolve(eq1,phiBC,[phibc(1,j-1)-deg2rad(10) phibc(1,j-1)+deg2rad(10)]));
            end
            % ----------计算phibc结束----------
            [xc,yc] = crank(xb,yb,r2,phibc(j));
            [xd,yd,phiCD,phiED] = RRR(xc,yc,xe,ye,r9,r3,1);
            [xf,yf] = rigid_body(xb,yb,phibc(j),a1,b1,1);
            [xg,yg] = rigid_body(xe,ye,phiED,a2,b2,-1);
            phiFG = atan2(yg-yf,xg-xf);
            [xh(j),yh(j)] = rigid_body(xf,yf,phiFG,a3,b3,1);     
        end
         x = [xa,xb,xc,xd,xe,xf,xg,xh(j)];
