%% ============== subfunctions of Assur Group Method ======================  
    function [x2,y2] = crank(x1,y1,l1,phi)
        % Motion law of the end of the driving component in the R-pair
        x2 = x1+l1*cos(phi);
        y2 = y1+l1*sin(phi);
    end
    function [x3,y3] = rigid_body(x1,y1,phi,a,b,M)
        % Find the C coordinates (x3, y3) of a point on a rigid body

        theta = atan(b/a); 
        x3 = x1 + sqrt(a^2+b^2)*cos(phi + M*theta);  
        y3 = y1 + sqrt(a^2+b^2)*sin(phi + M*theta);  % M = 1 means the point to be found (x3 y3) is on the top; otherwise, it is on the bottom
    end
    function [x3,y3,theta,phi3] = RRR(x1,y1,x2,y2,l2,l3,M)
        %For RRR secondary rod group, find the inner pair when the outer pair is known.
        %x1 y1 x2 y2 are the coordinates of the two outer pairs; l1 l2 are the lengths of the two rods; M=1 means normal installation
        %As shown in Figure RRR1, when normal installation, point C is on the left side of vector BD; when reverse installation, point C is on the right side of vector BD
        %As shown in Figure RRR2, when normal installation, theta = phi2 + alpha must be true; when normal installation, theta = phi2 - alpha must be true
        d = sqrt((x2-x1)^2 + (y2-y1)^2);
        phi2 = atan2((y2-y1),(x2-x1));
        alpha = acos((l2^2+d^2-l3^2)/(2*l2*d));
        theta = phi2+M*alpha;%M = 1,待求点（x3 y3）在上；反之在下
        x3 = x1 + l2*cos(theta);
        y3 = y1 + l2*sin(theta);
        phi3 = atan2((y3-y2),(x3-x2));
    end
    function [a,b] = sss2ab(l1,l2,l3)
       C = acos((l3^2 + l1^2 - l2^2)/(2*l3*l1)); 
       a = l3 * cos(C);
       b = l3 * sin(C);
    end
    function [r1,r2,r3] = sab2sss(r1,a,b)
